    private void cixActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cixActionPerformed
    int firstname=Integer.valueOf(this.firstname.getText());
        int secondname=Integer.valueOf(this.secondname.getText());
        result.setText(String.valueOf(firstname-secondname));
        
    }//GEN-LAST:event_cixActionPerformed

