    private void toplaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_toplaActionPerformed
       int firstname=Integer.valueOf(this.firstname.getText());
        int secondname=Integer.valueOf(this.secondname.getText());
        result.setText(String.valueOf(firstname+secondname));
        
    }//GEN-LAST:event_toplaActionPerformed

    private void vurActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_vurActionPerformed
      int firstname=Integer.valueOf(this.firstname.getText());
        int secondname=Integer.valueOf(this.secondname.getText());
        result.setText(String.valueOf(firstname*secondname));
        
    }//GEN-LAST:event_vurActionPerformed

    private void bolActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bolActionPerformed
    int firstname=Integer.valueOf(this.firstname.getText());
        int secondname=Integer.valueOf(this.secondname.getText());
        result.setText(String.valueOf((double)firstname/secondname));
        
    }//GEN-LAST:event_bolActionPerformed

